.calc_block5 {
	width: 100%;
	/*outline: 1px solid rgb(192, 175, 175);*/
	margin: 40px auto 0;
	/*background-image: linear-gradient(to top, rgba(0, 0, 0, 0.05) 0%, rgba(0, 0, 0, 0.25) 0%, #5C5A5A 150%);*/
	padding-bottom: 40px;
	position: relative;
	z-index: 2;
}
/* <<<<<<<<<<<<<<<<<<<<<<<<<<<< Block_2 >>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>> */
.block_5 {
	background-image: linear-gradient(to right, rgba(62, 62, 145, 0.544), rgba(62, 62, 145, 0.544)), url(contact.37bc6bab.jpg);
	width: 100%;
	min-height: 100vh;
	text-align: center;;
	background-repeat: no-repeat;
	background-size: cover;
	padding: 15px 0;
}
.block5_text, .contact_form {
	display: inline-block;
	vertical-align: top;
	padding: 5px;
	width: 45%;
}
.block5_main_text {
	text-transform: uppercase;
	font-size: 30px;
	font-weight: 700;
	line-height: 72px;
	letter-spacing: 2.4px;
	margin: 0;
}
.block5_additional_text {
	font-size: 20px;
	text-align: left;
}
.logo5 {
	width: 140px;
	position: relative;
	color: rgb(255, 217, 0);
	cursor: pointer;
	transition: .3s;
}
.logo5:hover {
	opacity: .9;
	transform: scale(1.01)
}
.contact_form  {
	margin-left: 25px;
	padding-top: 10px;
}
form {
	font-family: 'Roboto', sans-serif;
	height: 246px;
	border: 4px solid #fff;;
	width: 100%;
	color: #fff;
}
#applicationName, #applicationLastname, #applicationEmail, #applicationMessage {
	background-color: transparent;
	color: #fff;
	font-size: 18px;
	resize: none;
	width: 100%;
}
#applicationName {
	margin-right: -5px;
	height: 44px;
	border-left: none;
	border-right: none;
}
#applicationLastname {
	height: 44px;
	border-left: none;
	border-right: none;
}
#applicationEmail {
	height: 44px;
	border-left: none;
	border-right: none;
}
#applicationMessage {
	height: 107px;
	color: #fff;
	padding-top: 10px;
	overflow: auto;
	border-left: none;
	border-right: none;
}
input::-webkit-input-placeholder, textarea::-webkit-input-placeholder { 
	color: #fff;
}
.contact_form p {
	padding: 15px;
	color: #fff;
}
#btn-send {
	background: transparent;
	margin-top: 20px;
	color: #fff;
	font-size: 20px;
	font-weight: 400;
	font-family: 'Roboto', sans-serif;
	border: 1px solid #E4DFDB;
	padding: 8px 10px 8px 10px;
	transition: .2s;
}
#btn-send:hover {
	color: rgba(255, 213, 0, 0.949);
	border: 1px solid rgba(255, 213, 0, 0.949);
}
textarea, input {
	appearance: none;
	outline: 0;
	box-shadow: none;
	padding-left: 15px;
}
input:focus, textarea:focus {
	border: 1.5px solid rgba(255, 217, 0, 0.66);
}
.footer {
	min-height: 10vh;
	text-align: center;
	background: rgba(255, 213, 0, 0.949);
}
.footer img {
	width: 80px;
	height: 80px;
	padding: 20px;
	margin-left: 20px;
	margin-top: 10px;
	opacity: 0.9;
	border-radius: 50%;
}
.footer img:hover {
	opacity: 1;
	cursor: pointer;
	background-color: rgb(255, 223, 0);
	box-shadow: 0 0 5px rgba(0,0,0,0.5);
	transition: 0.5s;
}
/* >>>>>>>> Modal window <<<<<<<<<<<<< */
.blur {
	filter: blur(8px);
	min-height: 100vh;
	background-image: linear-gradient(to right, rgba(62, 62, 145, 0.544), rgba(62, 62, 145, 0.544)), url(contact.37bc6bab.jpg);
	background-repeat: no-repeat;
	background-size: cover;
	text-align: center;
	background-color: #000000e0;
}
.null {
	height: 100vh;
	background-image: linear-gradient(to right, rgba(62, 62, 145, 0.544), rgba(62, 62, 145, 0.544)), url(contact.37bc6bab.jpg);
	background-repeat: no-repeat;
	background-size: cover;
	text-align: center;
	background-color: #000000e0;
}
.modal {
	font-family: 'Roboto', sans-serif;
	position: fixed;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	background: rgba(0,0,0,.5);
	display: none;
	z-index: 9999;
}
.modal:target {
	font-family: 'Roboto', sans-serif;
	display: block;
	pointer-events: auto;
}
.popup {
	width: 500px;
	position: relative;
	margin: 10% auto;
	padding: 20px;
	background: #efedeb;
	overflow: hidden;
}
.close {
	color: #000000;
	position: absolute;
	font-size: 51px;
	top: -6px;
	right: 8px;
	text-decoration: none;
	display: block;
}
.modal-img {
	transform: translate(-90px, -87px);
	width: 305px;
	}
.modal-img-rotate {
	transform: rotate(-4deg);
	height: 240px;
}
.modal-text, .modal-img-rotate {
	display: inline-block;
	vertical-align: top;
	width: 44%;
}
.modal-text-main {
	font-size: 35px;
	text-align: center;
	font-weight: bold;
	padding-top: 20px;
	color: black;
}
.modal-text-little {
	font-size: 18px;
	font-weight: normal;
	padding-top: 20px;
	color: black;
}
.btn_home {
	text-decoration: none;
	text-align: center;
	border: 1px solid #E4DFDB;
	color: #fff;
	font-size: 20px;
	padding: 15px 15px;
	padding-left: 78px;
	padding-right: 78px;
	transition: 0.5s;
	display: block;
	background: #161414e0;
	cursor: pointer;
	padding: 10px;
	width: 290px;
	margin: 0 auto;
}
.btn_home:hover {
	border: 1px solid #E4DFDB;
	background: #161414e8;
	transition: 0.3s;
}
