.wrapper2 {
	max-width: 1340px;
	margin: 0 auto;
	padding: 10px 40px;
	min-height: 100vh;
}
.all-items-wrap {
	min-height: 100vh;
}
.description-link{
	text-decoration: none;
	color: black;
}
/* <<<<<<<<<<<<<<<<<<<<<<<<<<<< Block_2 >>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>> */
.calc_block2 {
	width: 100%;
	min-height: 100vh;
	outline: 1px solid rgb(192, 175, 175);
	margin: 40px auto 0;
	background-image: linear-gradient(to top, rgba(0, 0, 0, 0.05) 0%, rgba(0, 0, 0, 0.25) 0%, #5C5A5A 150%);
	padding-bottom: 10px;
	position: relative;
	z-index: 2;
}
.block2-text {
	padding-left: 15px;
}
.block2-list {
	padding-left: 50px;
	padding-right: 5px;
}
.block_2 {
	width: 100%;
/*	min-height: 100vh;*/
	padding: 15px 0;
	background-image: linear-gradient(to right, rgba(62, 62, 145, 0.544), rgba(62, 62, 145, 0.544)), url(img-block2.32f02f6c.jpg);
	background-size: cover;
	text-align: center;
	padding: 0;
}
.block2_main_text {
	margin: 0 auto;
	color: #E4DFDB;
	padding-top: 20px;
	padding-bottom: 20px;
	font-size: 1.5em;
}
.block2_additional_text {
	text-align: center;
	padding-bottom: 15px;
}
.description {
	height: 98px;
	width: 310px;
	border: 1px solid black;
	padding-top: 15px;
	margin-top: 15px;
	text-align: left;
	overflow: hidden;
	background-color: #efedeb;
	font-size: 18px;
	position: absolute;
}
.website-image, .shop-image, .landing-image {
	border: 1px solid black;
	margin-top: 20px;
	background-repeat: no-repeat;
	background-position: top;
	background-color: #efedeb;
	width: 310px;
	margin-top: 150px;
	min-height: 65vh;
}
.landing-image {
	background-image: url(land.26a812c9.jpg);
	background-size: 63%;
}
.website-image{
	background-image: url(corp.96311c10.png);
	background-size: 92%;
}
.shop-image {
	background-image: url(shop.10959dc8.jpg);
	background-size: 80%;
}
.all-block2 {
	display: inline-block;
	vertical-align: top;
	margin: 0 auto;
	padding: 20px;
}
.all-block2:hover .description {
	background: rgb(244, 223, 119);
	height: 290px;
	transition: .7s ease-in-out;
	box-shadow: 0 0 10px rgba(0,0,0,0.5);
	cursor: pointer;
}
.shop-image:hover, .website-image:hover, .landing-image:hover {
	height: 65vh;
	transition: .5s ease-in;
	z-index: 1;
	transform: translate(0px, -85px);
	cursor: pointer;
}
.div-icon-block2 {
	margin: 0 auto;
	width: 60px;
}
.all-block2:hover .div-icon-block2 {
	z-index: 5;
	-webkit-animation: pulsing 2s infinite;
	animation: pulsing 3s infinite;
}
@-webkit-keyframes pulsing {
	0% {
	-webkit-transform: scale(0.5, 0.5);
	transform: scale(0.5, 0.5)
	}
	50% {
	-webkit-transform: scale(1.0, 1.0);
	transform: scale(1.0, 1.0);
	}
	100% {
	-webkit-transform: scale(0.5, 0.5);
	transform: scale(0.5, 0.5);
	}
}
@keyframes pulsing {
	0% {
	-webkit-transform: scale(0.5, 0.5);
	transform: scale(0.5, 0.5)
	}
	50% {
	-webkit-transform: scale(1.0, 1.0);
	transform: scale(1.0, 1.0);
	}
	100% {
	-webkit-transform: scale(0.5, 0.5);
	transform: scale(0.5, 0.5);
	}
}
